module.exports =
{
    rules:
    {
        'no-program':
        {
            create(context)
            {
                const listeners =
                {
                    Program(node)
                    {
                        context.report
                        (
                            {
                                node,
                                message: 'Program is disallowed.',
                            },
                        );
                    },
                };
                return listeners;
            },
        },
    },
};
